import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import axios from 'axios';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = { profil: [] };

    }
    componentDidMount() {

        axios.get('http://localhost:8080/administration')
            .then(response => {
                console.log("login ity o", response.data);

                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }

  render() {
    return (

            <div className="container-fluid">
    <div className="row">
        <div className="col-md-4"></div>
        <div className="col-md-4" id="loginrow">
        <MDBRow>
          <MDBCol md="12">
            <form noValidate onSubmit={this.onSubmit}>
              <p className="h5 text-center mb-4" id="loginp">Connexion D'admin du site</p>
              <div className="grey-text">
                <MDBInput
                  label="Votre adresse e-mail"
                  icon="envelope"
                  group
                  type="email"
                  success="right"
                  id="email"
                  onChange={this.onChange}
                  value={this.state.email}
                  className={classnames("", {
                    invalid: errors.email || errors.emailnotfound
                  })}

                />
                 <span className="red-text">
                  {errors.email}
                  {errors.emailnotfound}
                </span>
                <MDBInput
                  label="Votre mot de passe"
                  icon="lock"
                  group
                  type="password"
                  onChange={this.onChange}
                  value={this.state.password}
                  id="password"
                  className={classnames("", {
                    invalid: errors.password || errors.passwordincorrect
                  })}
                />
                 <span className="red-text">
                  {errors.password}
                  {errors.passwordincorrect}
                </span>
              </div>
              <div className="text-center">
                <MDBBtn type="submit" id="inscrire-btn">Se Connecter</MDBBtn>
              </div>
            </form><br/>
            <span>Si vous n'êtes pas encore de compte.Inscrivez-vous ici :</span> <Link to="/register" className="btn btn-primary" id="inscrire-btn">S'inscrire</Link><br/>
            Vers &nbsp; <Link to="/">Accueil</Link>
          </MDBCol>
        </MDBRow>
        </div>
        <div className="col-md-4"></div>
    </div>

        </div>

    );
  }
}
export default Login;
