import React, { Component } from "react";
import axios from 'axios';
class Panier extends Component {
  constructor(props) {
    super(props);
    this.state = {
        profil: []
    };

}

//   componentDidMount() {
//     axios.get('http://localhost:8080/panier/'+ localStorage.getItem('id_user'))
//         .then(response => {
//         this.setState({ profil: response.data });
//         })
//         .catch(function (error) {
//             console.log(error);
//         })
// }

componentDidMount() {
  axios.get('http://localhost:8080/'+this.props.match.params.id)
      .then(response => {
            // console.log("donné à modifier" , response.data)
          this.setState({
              titre: response.data.titre,
              artiste: response.data.artiste,
              description :  response.data.description,
              date: response.data.date,
              genre: response.data.genre,
              prix: response.data.prix
          })
      });
}

  render() {
    return (
      <div className="container-fluid">
        <div id="panierdiv">
        <div id="">
                <h4 id="h4tableau"> Votre Panier</h4>
                <table className="table table-striped table-bordered table-hover" id="table">
                    <thead>
                        <tr>
                            {/* <th className="thtab">Images</th> */}
                            <th className="thtab">Artiste</th>
                            <th className="thtab">Titre</th>
                            <th className="thtab">Description</th>
                            <th className="thtab">Date de publication</th>
                            <th className="thtab">Genre</th>
                            <th className="thtab">Prix</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {
                                return <tr key={obj._id}>
                                    {/* <td><img id="imagetab" width="100px" height="90px" src={'http://localhost:8080/atelier/' + obj.photo_produit} alt={obj.photo_produit} /></td> */}
                                    <td>{obj.artiste}</td>
                                    <td>{obj.titre}</td>
                                    <td>{obj.description}</td>
                                    <td>{obj.date}</td>
                                    <td>{obj.genre}</td>
                                    <td>{obj.prix}  Ar</td>


                                </tr>
                            })) : ('Votre panier est vide')
                        }
                    </tbody>
                </table>

            </div>
        </div>   
        </div>
    );
  }
}
export default Panier;
