import React, { Component } from "react";
import { Redirect } from 'react-router-dom'
import { MDBRow, MDBCol, MDBInput, MDBBtn} from "mdbreact";
import axios from 'axios';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            username: "",
            password: "",
            profil: [] };

    }

    state = {
        redirect: false
      }

      setRedirect = () => {
        this.setState({
          redirect: true
        })
      }

      renderRedirect = () => {
        // if (this.state.redirect) {
        //   return <Redirect to='/dashboard' />
        // }
        if(this.state.redirect){
            return <Redirect to='/dashboard' />
        }
        else{
            return <Redirect to='/' />
        }
      }


    onChange = e => {
        this.setState({ [e.target.id]: e.target.value });
      };


    componentDidMount() {

        axios.get('http://localhost:8080/administration')
            .then(response => {

                localStorage.setItem('username', response.data[0].username);

                console.log("Storage username",localStorage.getItem('username'));

                this.setState({ profil: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })
    }

  render() {
    return (

            <div className="container-fluid">
            {this.renderRedirect()}
    <div className="row">
        <div className="col-md-4"></div>
        <div className="col-md-4" id="loginrow">
        <MDBRow>
          <MDBCol md="12">
            <form  onSubmit={this.onSubmit}>
              <p className="h5 text-center mb-4" id="loginp">Connexion de l'administration du site</p>
              <div className="grey-text">
                <MDBInput
                  label="Nom d'utilisateur"
                  icon="envelope"
                  group
                  type="email"
                  success="right"
                  id="email"
                  name="username"
                  onChange={this.onChange}
                  value={this.state.username}
                  required
                />

                <MDBInput
                  label="mot de passe"
                  icon="lock"
                  group
                  type="password"
                  onChange={this.onChange}
                  value={this.state.password}
                  id="password"
                  name="password"
                  required
                />

              </div>
              <div className="text-center">
                <MDBBtn type="submit" id="inscrire-btn">Se Connecter</MDBBtn>
              </div>
            </form><br/>
          </MDBCol>
        </MDBRow>
        </div>
        <div className="col-md-4"></div>
    </div>

        </div>

    );
  }
}
export default Login;
