import React, { Component } from "react";
import axios from 'axios';
import StripeCheckout from 'react-stripe-checkout'
class Panier extends Component {
  constructor(props) {
    super(props);
    this.state = {
        profil: []
    };

}

onToken = (token) => {
    fetch('/save-stripe-token', {
      method: 'POST',
      body: JSON.stringify(token),
    }).then(response => {
      response.json().then(data => {
        alert(`We are in business, ${data.email}`);
      });
    });
  }

  componentDidMount() {
    axios.get('http://localhost:8080/panier/'+ localStorage.getItem('id_user'))
        .then(response => {
        this.setState({ profil: response.data });
        })
        .catch(function (error) {
            console.log(error);
        })
}


  render() {
    return (
      <div className="container-fluid">
        <div id="panierdiv">
        <div id="">
                <h4 id="h4tableau"> Votre Panier</h4>
                <table className="table table-striped table-bordered table-hover" id="table">
                    <thead>
                        <tr>
                            {/* <th className="thtab">Images</th> */}
                            <th className="thtab">Artiste</th>
                            <th className="thtab">Titre</th>
                            <th className="thtab">Genre</th>
                            <th className="thtab">Prix</th>
                            <th className="thtab">Payment en ligne sur Stripe.com</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            (this.state.profil.length > 0) ? (this.state.profil.map((obj) => {
                                return <tr key={obj._id}>
                                    {/* <td><img id="imagetab" width="100px" height="90px" src={'http://localhost:8080/atelier/' + obj.photo_produit} alt={obj.photo_produit} /></td> */}
                                    <td>{obj.artiste}</td>
                                    <td>{obj.titre}</td>

                                    <td>{obj.genre}</td>
                                    <td>{obj.prix}  Ar</td>

                                    {/* <td><StripeCheckout  token={this.onToken}
                                        stripeKey="pk_test_vrVww8SlifCAMUq8i5Ls6QgR00jHOsxvxx"
                                        /></td> */}
                                        <StripeCheckout
  name="Three Comma Co." // the pop-in header title
  description="Big Data Stuff" // the pop-in header subtitle
  image="https://www.vidhub.co/assets/logos/vidhub-icon-2e5c629f64ced5598a56387d4e3d0c7c.png" // the pop-in header image (default none)
  ComponentClass="div"
  panelLabel="Give Money" // prepended to the amount in the bottom pay button
  amount={1000000} // cents
  currency="USD"
  stripeKey="pk_test_vrVww8SlifCAMUq8i5Ls6QgR00jHOsxvxx"
  locale="zh"
  email="info@vidhub.co"
  // Note: Enabling either address option will give the user the ability to
  // fill out both. Addresses are sent as a second parameter in the token callback.
  shippingAddress
  billingAddress={false}
  // Note: enabling both zipCode checks and billing or shipping address will
  // cause zipCheck to be pulled from billing address (set to shipping if none provided).
  zipCode={false}
  alipay // accept Alipay (default false)
  bitcoin // accept Bitcoins (default false)
  allowRememberMe // "Remember Me" option (default true)
  token={this.onToken} // submit callback
  opened={this.onOpened} // called when the checkout popin is opened (no IE6/7)
  closed={this.onClosed} // called when the checkout popin is closed (no IE6/7)
  // Note: `reconfigureOnUpdate` should be set to true IFF, for some reason
  // you are using multiple stripe keys
  reconfigureOnUpdate={false}
  // Note: you can change the event to `onTouchTap`, `onClick`, `onTouchStart`
  // useful if you're using React-Tap-Event-Plugin
  triggerEvent="onTouchTap"
  >
  <button className="btn btn-primary">
   payer
  </button>
</StripeCheckout>
                                </tr>
                            })) : ('Votre panier est vide')
                        }
                    </tbody>
                </table>

            </div>
        </div>   
        </div>
    );
  }
}
export default Panier;
